func (c *current) onValueList2(first, rest interface{}) (interface{}, error) {
	out := []ast.Val{first.(ast.Val)}
	restSl := toAny(rest)
	if len(restSl) == 0 {
		return nil, fmt.Errorf("unnecessary parenthesis for only one value")
	}
	for _, v := range restSl {
		r := toAny(v)
		out = append(out, r[3].(ast.Val))
	}
	return out, nil
}

